SWEP.Base = "weapon_zs_base"

SWEP.HoldType = "ar2"

SWEP.ViewModel = "models/weapons/cstrike/c_rif_ak47.mdl"
SWEP.WorldModel = "models/weapons/w_rif_ak47.mdl"
SWEP.UseHands = true

SWEP.Primary.Damage = 22
SWEP.Primary.NumShots = 2
SWEP.Primary.Delay = 0.105
SWEP.Primary.Sound = Sound("") 
SWEP.Primary.ClipSize = 32
SWEP.Primary.Automatic = true
SWEP.Primary.Ammo = "ar2"

GAMEMODE:SetupDefaultClip(SWEP.Primary)
SWEP.ReloadSpeed = 1.175

SWEP.ConeMax = 4.25
SWEP.ConeMin = 1.5

SWEP.IronSightsPos = Vector(-1.5, 12, 2.75)
SWEP.IronSightsAng = Vector(0, 1.766, 0)

SWEP.WalkSpeed = SPEED_SLOW

SWEP.Tier = 6
SWEP.MaxStock = 1

SWEP.EnableChargingAbility = true

SWEP.FullChargeText = "ЛКМ: Увеличенный урон!"
SWEP.ChargeText = "Хедшоты"

SWEP.ChargeColor = Color(255, 128, 0)

SWEP.ChargeGain = 0.115

function SWEP:PrimaryAttack()
	if not self:CanPrimaryAttack() then return end

	local owner = self:GetOwner()

	local damage = self.Primary.Damage * (self.AbilityActive and 1.25 or 1)

	self:SetNextPrimaryFire(CurTime() + self:GetFireDelay())

	self:EmitFireSound()
	self:TakeAmmo()
	self:ShootBullets(damage, self.Primary.NumShots, self:GetCone())
	self.IdleAnimation = CurTime() + self:SequenceDuration()
end

function SWEP.BulletCallback(attacker, tr, dmginfo)
	local ent = tr.Entity
	local wep = attacker:GetActiveWeapon()
	if tr.HitGroup == HITGROUP_HEAD and not wep.AbilityActive then
		if ent:IsValidZombie() and wep.EnableChargingAbility then
			local damage = (dmginfo:GetDamage() * wep.ChargeGain)
			local intd = math.min(100 - damage, wep:GetDTInt(DT_PLAYER_INT_ABILITYWEAPON))
			wep:SetDTInt(DT_PLAYER_INT_ABILITYWEAPON, intd + damage)
		end
	end
end

local nexttime = 0
function SWEP:Think()
	self.BaseClass.Think(self)

	local time = CurTime()

	local owner = self:GetOwner()
	local wep = owner:GetActiveWeapon()

	local intd = math.min(100, wep:GetDTInt(DT_PLAYER_INT_ABILITYWEAPON))

	if wep.AbilityActive and time >= nexttime then
		nexttime = time + 0.15
		wep:SetDTInt(DT_PLAYER_INT_ABILITYWEAPON, math.max(0, intd - 0.175))
	end

	if intd >= 100 then
		wep.AbilityActive = true
	elseif intd <= 0 then
		wep.AbilityActive = false
	end
end

function SWEP:EmitFireSound()
	self:EmitSound("weapons/zs_tartaros/tartaros.ogg", 72, 108, 0.45, CHAN_AUTO + 20)
	self:EmitSound("weapons/zs_scar/scar_fire1.ogg", 70, 103, 0.6)
end